			<section class="our-team" id="restaurantmap">				<div class="container" id="team-container">										<div class="map-section" id="map_section">						<div class="map-input" id="loc_input">															<h6 class="map-header">Restaurant Distribution</h6>								<p class="warning" id="warning"></p>								<p class="map-input-text1">City:</p>								<select class="map-option-select" id="city">								</select>								<p class="map-input-text2">Zip Code:</p>								<p class="zipcode-reminder" id="zipcode_reminder"></p>								<input type="text" id="zip_code"/><br>								<p class="map-input-text3">Category:</p>								<select class="map-option-select"  id="category">								</select><br>								<button class="button-map" id = "submitBtn" >submit</button>													</div>						<div class="map-div" id ="map_div">						</div>					</div>									 </div>			 </section>			<?php	global $wpdb;	$cities = array();	$sql = 'SELECT city FROM city';	$results = $wpdb->get_results($sql);	foreach($results as $result){		array_push($cities,$result->city);	}	echo '<script> var cities='.json_encode($cities).'</script>';?><script type="text/javascript">	var list = "";		for(var i =0;i < cities.length; i++){		list+='<option>'+cities[i]+'</option>';	}	document.getElementById('city').innerHTML=list;	var categories = ["Chinese","Korean","Japanese","American","Mexican","Brazilian","Italian","Indian","French","Vietnamese","Turkish","Greek","Spanish","Irish","Canadian","Thai","Persian","Argentine","Cambodian","Cuban","Australian"];	var list2 = "";	list2+='<option>All</option>';	for(i=0;i<categories.length;i++){		list2+='<option>'+categories[i]+'</option>';	}	document.getElementById('category').innerHTML=list2;	$(document).ready(function() {		$('#submitBtn').click(function() {			var str=[];			str.push($('#city').val());			str.push($('#zip_code').val());			str.push($('#category').val());			getLocations(str);			document.getElementById('zipcode_reminder').innerHTML="";			document.getElementById("warning").innerHTML = "";		});	});	$(document).ready(function() {		$('#zip_code').click(function() {			document.getElementById('zipcode_reminder').innerHTML="*Specicy one or left blank.";		});	});	$(document).ready(function() {		$('#category').click(function() {			document.getElementById('zipcode_reminder').innerHTML="";		});	});	$(document).ready(function() {		$('#city').click(function() {			document.getElementById('zipcode_reminder').innerHTML="";		});	});	var map = new google.maps.Map(document.getElementById('map_div'), {      zoom: 4,      center: new google.maps.LatLng(41.8369, -87.6847),      mapTypeId: google.maps.MapTypeId.ROADMAP,	  scrollwheel:false    });		function getLocations(strArr){		map = new google.maps.Map(document.getElementById('map_div'), {			zoom: 4,			center: new google.maps.LatLng(41.8369, -87.6847),			mapTypeId: google.maps.MapTypeId.ROADMAP		});			var xmlhttp = new XMLHttpRequest();			xmlhttp.onreadystatechange = function() {				if (xmlhttp.readyState == 4 && xmlhttp.status == 200) {					//document.getElementById("warning").innerHTML = xmlhttp.responseText;					var locations = JSON.parse(xmlhttp.responseText);					if(locations['0'].length > 0){										if(locations.length > 0){						for (i = 0; i < locations['0'].length; i++) {							var infowindow = new google.maps.InfoWindow();							var content = locations['2'][i]+' '+locations['3'][i];							marker=new google.maps.Marker({							position: new google.maps.LatLng(locations['0'][i], locations['1'][i]),							map: map							});							google.maps.event.addListener(marker,'click', (function(marker,content,infowindow){ 								return function() {									infowindow.setContent(content);									infowindow.open(map,marker);								};								})(marker,content,infowindow)); 										}					if(strArr[1].length==0){						map.setZoom(10);					}else{						map.setZoom(14);					}					map.setCenter(new google.maps.LatLng(locations['0'][0], locations['1'][0]));					}					}else{						document.getElementById("warning").innerHTML = "No records found, please enter again.";					}				}			};        xmlhttp.open("GET", "<?php echo esc_url( get_template_directory_uri() ); ?>/sections/getLocations.php?q=" + strArr, true);        xmlhttp.send();    	}</script> 